const CDNURL = "https://cdn.discordapp.com/";
function getFormat(hash, format, allowsGIF = true) {
    if (!allowsGIF && format === ".gif")
        throw new Error("Invalid image format");
    if (typeof format !== "undefined")
        return format;
    if (hash.startsWith("a_"))
        return ".gif";
    return ".jpg";
}
function getSize(size) {
    if (typeof size === "undefined")
        return "";
    return `?size=${size}`;
}
export function customEmojiURL(emojiId, options) {
    return `${CDNURL}emojis/${emojiId}${getFormat(emojiId, options?.format)}${getSize(options?.size)}`;
}
export function guildIconURL(guildId, guildIcon, options) {
    return `${CDNURL}icons/${guildId}/${guildIcon}${getFormat(guildIcon, options?.format)}${getSize(options?.size)}`;
}
