export const DiscordEpoch = 1420070400000n;
export function extractTimestampFromId(id) {
    return Number((BigInt(id) >> 22n) + DiscordEpoch);
}
export function isDeletableMessage(type, canManageMessages = false) {
    if (type === 24)
        return canManageMessages;
    return type === 0
        || (type >= 6 && type <= 20)
        || type >= 22;
}
